import React from 'react';
import { useFormik, Formik } from 'formik';
import './contact.css';

const Contact = () => {

  const formik = useFormik({
    initialValues: {
      primaryName: '',
      lastName: '',
      email: '',
      comment: '',
    },
    onSubmit: values => {
      alert(
        `
        Hola ${values.primaryName} ${values.lastName}
        Su mensaje fue enviado con exito,
        verifique su bandeja de entrada
        de su correo ${values.email}`
      );
        return;
    },
  });
  return (
    <Formik
      onSubmit={()=>{
      console.log('Enviado');
      }}
    >
      {()=>(
      <form className='box-form'>
          <div className='box-form'>
              <div>
                  <label htmlFor="primaryName">Nombre: </label>
                  <input
                    id="primaryName"
                    name="primaryName"
                    type="text"
                  />
              </div>
              <div>
                  <label htmlFor="lastName">Apellido: </label>
                  <input
                    id="lastName"
                    name="lastName"
                    type="text"
                  />
              </div>
              <div>
                  <label htmlFor="email">Email: </label>
                  <input
                    id="email"
                    name="email"
                    type="email"
                  />
              </div>
              <div>
                  <label htmlFor="textarea">Comentario: </label>
                  <textarea
                    id="comment"
                    name="comment"
                    type="comment"
                  />
              </div>      
          </div>
          <button type="submit">Enviar</button>
          </form>
    )}
    </Formik>

);
};
export default Contact;
