import React from 'react';
import Button from '@mui/material/Button';
import Stack from '@mui/material/Stack';
import { useFormik } from 'formik';
import './contact.css';

const Contact = () => {

  const formik = useFormik({
    initialValues: {
      primaryName: '',
      lastName: '',
      email: '',
      comment: '',
    },
    onEnviar: values => {
      alert(
        `
        Hola ${values.primaryName} ${values.lastName}
        Su mensaje fue enviado con exito,
        verifique su bandeja de entrada
        de su correo ${values.email}`
      );
        return;
    },
  });
  return (
    <form onSubmit={formik.handleSubmit} className="form-contact">
      <label htmlFor="primaryName">Nombre: </label>
      <input
        id="primaryName"
        name="primaryName"
        type="text"
        onChange={formik.handleChange}
        value={formik.values.primaryName}
      />

      <label htmlFor="lastName">Apellido: </label>
      <input
        id="lastName"
        name="lastName"
        type="text"
        onChange={formik.handleChange}
        value={formik.values.lastName}
      />

      <label htmlFor="email">Email: </label>
      <input
        id="email"
        name="email"
        type="email"
        onChange={formik.handleChange}
        value={formik.values.email}
      />
      
      <label htmlFor="textarea">Comentario: </label>
      <textarea
        id="comment"
        name="comment"
        type="comment"
        onChange={formik.handleChange}
        value={formik.values.comment}
      />

      {/* <button type="Enviar">Enviar</button> */}
      <Button type="onSubmit" color="inherit">Enviar</Button>
    </form>
  );
};
export default Contact;
