import React from 'react';
import { Formik } from 'formik';
import './contact.css';

const Contact = () => {

  return (
    <>
      <Formik
        initialValues = {{
          primaryName: '',
          lastName: '',
          email: '',
          comment: ''
        }}
        validate={ data => 
          {
          let errorsInput = {};
          if (!data.primaryName){
            errorsInput.primaryName = 'No debes de dejar en blanco el campo nombre';
            } else if (!/^[A-Za-z ]{1,40}$/.test(data.primaryName)){
              errorsInput.primaryName = 'Solo se acepta letras y espacios';
            };
          if (!data.lastName){
            errorsInput.lastName='No debes de dejar en blanco el campo apellido';
            } else if (!/^[A-Za-z ]{1,40}$/.test(data.lastName)){
              errorsInput.lastName = 'Solo se acepta letras y espacios';
            };
          if (!data.email){
            errorsInput.email='No debes de dejar en blanco el campo mail';
            } else if (!/^[A-Za-z0-9.+-]+@[A-Za-z0-9.+-]+\.[A-Za-z0-9.+-]+$/.test(data.email)){
              errorsInput.email = 'Es necesario una direcci칩n email v치lida';
            };
          if (!data.comment){
            errorsInput.comment='No debes de dejar en blanco el campo comentario';
            } 
            // else if(!/^[A-Za-z0-9_-.+]{200}$/.test(data.comment)){
          //     errorsInput.comment = 'Comentario inv치lido, por favor revisa tu mensaje';
          //   };
            return errorsInput;
          }
        }
        onSubmit={(data)=>{
        console.log(data);
        }}
      >
        {({handleSubmit, values, handleChange, handleBlur, errors})=>(
        <div className='box-form'>
        <form className='form-contact' onSubmit={ handleSubmit}>
          <div>
          <h2>Contactate Conmigo</h2>
          </div>
          <div>
              <label htmlFor='primaryName'>Nombre: </label>
              <input
                id = 'primaryName'
                name = 'primaryName'
                type = 'text'
                value = {values.primaryName}
                onChange = {handleChange}
                onBlur = {handleBlur}
              />
              {errors.primaryName && <div className='errors'>{errors.primaryName}</div>}
          </div>
          <div>
              <label htmlFor = 'lastName'>Apellido: </label>
              <input
                id = 'lastName'
                name = 'lastName'
                type = 'text'
                value = {values.lastName}
                onChange = {handleChange}
                onBlur = {handleBlur}
              />
              {errors.lastName && <div className='errors'>{errors.lastName}</div>}
          </div>
          <div>
              <label htmlFor = 'email'>Email: </label>
              <input
                id = 'email'
                name = 'email'
                type = 'email'
                value = {values.email}
                onChange = {handleChange}
                onBlur = {handleBlur}
              />
              {errors.email && <div className='errors'>{errors.email}</div>}
          </div>
          <div>
              <label htmlFor = 'textarea'>Comentario: </label>
              <textarea
                id = 'comment'
                name = 'comment'
                type = 'comment'
                value = {values.comment}
                onChange = {handleChange}
                onBlur = {handleBlur}
              />
              {errors.comment && <div className='errors'>{errors.comment}</div>}
          </div>      
            <button type = 'submit'>Enviar</button>
            </form>
          </div>
      )}
      </Formik>
    </>
);
};
export default Contact;
