import React from 'react';
import { useFormik, Formik } from 'formik';
import './contact.css';

const Contact = () => {

  return (
    <>
      <Formik
        initialValues={{
          primaryName: '',
          lastName: '',
          email: '',
          comment: ''
        }}
        validate={(data)=>{
          let errorsInput={};
          if (!data.primaryName || !data.lastName || !data.email || !data.comment){
            error.primaryName='No debes de dejar en blanco los campos';
            error.lastName='No debes de dejar en blanco los campos';
            error.email='No debes de dejar en blanco los campos';
            error.comment='No debes de dejar en blanco los campos';
          }
        }}
        onSubmit={(data)=>{
        console.log(data);
        }}
      >
        {({handleSubmit, values, handleChange, handleBlur})=>(
        <form className='box-form' onSubmit={ handleSubmit}>

            <div className='box-form'>
                <div>
                    <label htmlFor="primaryName">Nombre: </label>
                    <input
                      id="primaryName"
                      name="primaryName"
                      type="text"
                      value={values.primaryName}
                      onChange={handleChange}
                      onBlur={handleBlur}
                    />
                </div>
                <div>
                    <label htmlFor="lastName">Apellido: </label>
                    <input
                      id="lastName"
                      name="lastName"
                      type="text"
                      valus={values.lastName}
                      onChange={handleChange}
                      onBlur={handleBlur}
                    />
                </div>
                <div>
                    <label htmlFor="email">Email: </label>
                    <input
                      id="email"
                      name="email"
                      type="email"
                      values={values.email}
                      onChange={handleChange}
                      onBlur={handleBlur}
                    />
                </div>
                <div>
                    <label htmlFor="textarea">Comentario: </label>
                    <textarea
                      id="comment"
                      name="comment"
                      type="comment"
                      values={values.comment}
                      onChange={handleChange}
                      onBlur={handleBlur}
                    />
                </div>      
            </div>
            <button type="submit">Enviar</button>
            </form>
      )}
      </Formik>
    </>
);
};
export default Contact;
